mapper.update:
UPDATE users
username = #{username}, password = #{password}, real_name = #{realName}, email = #{email}, phone = #{phone}, user_type = #{userType}, status = #{status}, update_time = NOW()
WHERE user_id = #{userId}

mapper.select:
SELECT * FROM users
WHERE status = #{status}
ORDER BY username

SELECT * FROM users
WHERE user_type = #{userType}
ORDER BY username

SELECT * FROM users

    AND username LIKE CONCAT('%', #{username}, '%')
    AND real_name LIKE CONCAT('%', #{realName}, '%')
    AND user_type = #{userType}
    AND status = #{status}

ORDER BY create_time DESC

    LIMIT #{limit} OFFSET #{start}

SELECT COUNT(*) FROM users

    AND username LIKE CONCAT('%', #{username}, '%')
    AND real_name LIKE CONCAT('%', #{realName}, '%')
    AND user_type = #{userType}
    AND status = #{status}
